export class UserService {
  findById(id: string): { id: string; email: string } | null {
    if (id === '1') {
      return { id: '1', email: 'user@test.com' };
    }
    return null;
  }
}

files.test/auth.spec.ts:
import { AuthService } from '../src/auth.service';

describe('AuthService', () => {
  // @atom IA-001
  it('should authenticate user with valid credentials', () => {
    const service = new AuthService();
    const result = service.authenticate('user@test.com', 'validpass');
    expect(result.success).toBe(true);
    expect(result.token).toBeDefined();
  });

  // @atom IA-002
  it('should reject invalid credentials', () => {
    const service = new AuthService();
    const result = service.authenticate('user@test.com', 'wrongpass');
    expect(result.success).toBe(false);
  });

  // @atom IA-003
  it('should logout user and invalidate token', () => {
    const service = new AuthService();
    const result = service.logout('jwt-token-123');
    expect(result).toBe(true);
  });
});

files.test/user.spec.ts:
import { UserService } from '../src/user.service';

describe('UserService', () => {
  // @atom IA-004
  it('should find user by id', () => {
    const service = new UserService();
    const user = service.findById('1');
    expect(user).not.toBeNull();
    expect(user?.email).toBe('user@test.com');
  });

  // @atom IA-005
  it('should return null for non-existent user', () => {
    const service = new UserService();
    const user = service.findById('999');
    expect(user).toBeNull();
  });
});
